  calculateDuratin() {
    if (!this.isRecording) {
      this.duratin = 0
      this.durationDisplay = ""
      return
    }
    this.duratin += 1
    if (this.duratin === 20) {
      this.stopRecording()
    }

    const seconds = (this.duratin % 60).toString().padStart(2, '0');
    this.durationDisplay = seconds;
    setTimeout(() => {
      this.calculateDuratin()
    }, 1000)
  }
  stopRecording() {
    if (!this.isRecording) {
      return
    }

  }


